            add(s);
            s.velocity.X = 150;
            s.velocity.Y = 50;


            FlxG.follow(s, 10.0f);
            FlxG.followBounds(0, 0, 2000, 2000);



        }

        override public void update()
        {




            base.update();
        }


    }
}
